import React, {useRef} from "react";
import { useForm } from "react-hook-form";
import emailjs from '@emailjs/browser';

const Contact = () => {
  const form = useRef();

  const handleSubmit = (e) => {
    e.preventDefault();

    emailjs.sendForm('service_n9nc0mc', 'template_y0z61qt', form.current, 'tK1c7F3Y4wTrkkdZ-')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
  };


  return (
    <>
      <form className="contact_form" onSubmit={handleSubmit} ref={form}>
        <div className="first_row">
          <input
            type="text"
            placeholder="Name *"
          />
        </div>
        {/* End .first_row */}

        <div className="second">
          <input
            type="email"
            placeholder="Email *"
          />
        </div>
        {/* End .second */}

        <div className="third">
        <textarea
            placeholder="Message *"
          ></textarea>
        </div>
        {/* End .third */}
        <div className="edina_tm_button">
          <button type="submit" className="color">
            Submit
          </button>
        </div>
        {/* End tokyo_tm_button */}
      </form>
      {/* End contact */}
    </>
  );
};

export default Contact;
